$(document).ready(function () {
    $('#Pengalaman-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-Pengalaman-add').modal('show');
        $('#Pengalaman-create_btn').unbind();
        $('#Pengalaman-create_btn').on('click', function () {
            addPengalamanSave();
        });
    });
});

function addPengalamanSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				alamat:$('#Add-Pengalaman-Alamat').val(),
				bidangSubBidang:$('#Add-Pengalaman-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Add-Pengalaman-BidangSubBidangCode').val(),
				fileBast:$('#Add-Pengalaman-FileBast').val(),
				fileBastId:$('#Add-Pengalaman-FileBastId').val(),
				fileBuktiPengalaman:$('#Add-Pengalaman-FileBuktiPengalaman').val(),
				fileBuktiPengalamanId:$('#Add-Pengalaman-FileBuktiPengalamanId').val(),
				jenisMataUang:$('#Add-Pengalaman-JenisMataUang').val(),
				lokasi:$('#Add-Pengalaman-Lokasi').val(),
				namaPaketPekerjaan:$('#Add-Pengalaman-NamaPaketPekerjaan').val(),
				namaPenggunaJasa:$('#Add-Pengalaman-NamaPenggunaJasa').val(),
				nilaiKontrakPo:$('#Add-Pengalaman-NilaiKontrakPo').val(),
				noKontrakPo:$('#Add-Pengalaman-NoKontrakPo').val(),
				noTelepon:$('#Add-Pengalaman-NoTelepon').val(),
				selesaiKontrakPo:$('#Add-Pengalaman-SelesaiKontrakPo').val(),
				tglKontrakPo:$('#Add-Pengalaman-TglKontrakPo').val(),
				vendorId:$('#Add-Pengalaman-VendorId').val(),

            }
            RequestData('POST', `/v1/Pengalaman/add`, '#md-Pengalaman-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-Pengalaman-add').modal('hide');
                    getListPengalaman();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
