function editPengalamanDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-Pengalaman-edit').modal('show');

	$('#Edit-Pengalaman-Alamat').val(data.alamat);
	$('#Edit-Pengalaman-BidangSubBidang').val(data.bidangSubBidang);
	$('#Edit-Pengalaman-BidangSubBidangCode').val(data.bidangSubBidangCode);
	$('#Edit-Pengalaman-FileBast').val(data.fileBast);
	$('#Edit-Pengalaman-FileBastId').val(data.fileBastId);
	$('#Edit-Pengalaman-FileBuktiPengalaman').val(data.fileBuktiPengalaman);
	$('#Edit-Pengalaman-FileBuktiPengalamanId').val(data.fileBuktiPengalamanId);
	$('#Edit-Pengalaman-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-Pengalaman-Lokasi').val(data.lokasi);
	$('#Edit-Pengalaman-NamaPaketPekerjaan').val(data.namaPaketPekerjaan);
	$('#Edit-Pengalaman-NamaPenggunaJasa').val(data.namaPenggunaJasa);
	$('#Edit-Pengalaman-NilaiKontrakPo').val(data.nilaiKontrakPo);
	$('#Edit-Pengalaman-NoKontrakPo').val(data.noKontrakPo);
	$('#Edit-Pengalaman-NoTelepon').val(data.noTelepon);
	$('#Edit-Pengalaman-SelesaiKontrakPo').val(data.selesaiKontrakPo);
	$('#Edit-Pengalaman-TglKontrakPo').val(data.tglKontrakPo);
	$('#Edit-Pengalaman-VendorId').val(data.vendorId);


    $('#md-Pengalaman-edit').data('id', data.id);
    $('#Pengalaman-edit_btn').unbind();
    $('#Pengalaman-edit_btn').on('click', function () {
        editPengalamanSave();
    });
}

function editPengalamanSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				alamat:$('#Edit-Pengalaman-Alamat').val(),
				bidangSubBidang:$('#Edit-Pengalaman-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Edit-Pengalaman-BidangSubBidangCode').val(),
				fileBast:$('#Edit-Pengalaman-FileBast').val(),
				fileBastId:$('#Edit-Pengalaman-FileBastId').val(),
				fileBuktiPengalaman:$('#Edit-Pengalaman-FileBuktiPengalaman').val(),
				fileBuktiPengalamanId:$('#Edit-Pengalaman-FileBuktiPengalamanId').val(),
				jenisMataUang:$('#Edit-Pengalaman-JenisMataUang').val(),
				lokasi:$('#Edit-Pengalaman-Lokasi').val(),
				namaPaketPekerjaan:$('#Edit-Pengalaman-NamaPaketPekerjaan').val(),
				namaPenggunaJasa:$('#Edit-Pengalaman-NamaPenggunaJasa').val(),
				nilaiKontrakPo:$('#Edit-Pengalaman-NilaiKontrakPo').val(),
				noKontrakPo:$('#Edit-Pengalaman-NoKontrakPo').val(),
				noTelepon:$('#Edit-Pengalaman-NoTelepon').val(),
				selesaiKontrakPo:$('#Edit-Pengalaman-SelesaiKontrakPo').val(),
				tglKontrakPo:$('#Edit-Pengalaman-TglKontrakPo').val(),
				vendorId:$('#Edit-Pengalaman-VendorId').val(),

            }
            RequestData('PUT', `/v1/Pengalaman/edit/${$('#md-Pengalaman-edit').data('id')}`, '#md-Pengalaman-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-Pengalaman-edit').modal('hide');
                    getListPengalaman();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
