$(document).ready(function () {
    $('#RekeningBank-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-RekeningBank-add').modal('show');
        $('#RekeningBank-create_btn').unbind();
        $('#RekeningBank-create_btn').on('click', function () {
            addRekeningBankSave();
        });
    });
});

function addRekeningBankSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				fileSuratPernyataan:$('#Add-RekeningBank-FileSuratPernyataan').val(),
				fileSuratPernyataanId:$('#Add-RekeningBank-FileSuratPernyataanId').val(),
				jenisMataUang:$('#Add-RekeningBank-JenisMataUang').val(),
				kantorCabang:$('#Add-RekeningBank-KantorCabang').val(),
				namaBank:$('#Add-RekeningBank-NamaBank').val(),
				negara:$('#Add-RekeningBank-Negara').val(),
				noRekening:$('#Add-RekeningBank-NoRekening').val(),
				noRekeningFormat:$('#Add-RekeningBank-NoRekeningFormat').val(),
				pemegangRekening:$('#Add-RekeningBank-PemegangRekening').val(),
				vendorId:$('#Add-RekeningBank-VendorId').val(),

            }
            RequestData('POST', `/v1/RekeningBank/add`, '#md-RekeningBank-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-RekeningBank-add').modal('hide');
                    getListRekeningBank();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
