function editRekeningBankDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-RekeningBank-edit').modal('show');

	$('#Edit-RekeningBank-FileSuratPernyataan').val(data.fileSuratPernyataan);
	$('#Edit-RekeningBank-FileSuratPernyataanId').val(data.fileSuratPernyataanId);
	$('#Edit-RekeningBank-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-RekeningBank-KantorCabang').val(data.kantorCabang);
	$('#Edit-RekeningBank-NamaBank').val(data.namaBank);
	$('#Edit-RekeningBank-Negara').val(data.negara);
	$('#Edit-RekeningBank-NoRekening').val(data.noRekening);
	$('#Edit-RekeningBank-NoRekeningFormat').val(data.noRekeningFormat);
	$('#Edit-RekeningBank-PemegangRekening').val(data.pemegangRekening);
	$('#Edit-RekeningBank-VendorId').val(data.vendorId);


    $('#md-RekeningBank-edit').data('id', data.id);
    $('#RekeningBank-edit_btn').unbind();
    $('#RekeningBank-edit_btn').on('click', function () {
        editRekeningBankSave();
    });
}

function editRekeningBankSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				fileSuratPernyataan:$('#Edit-RekeningBank-FileSuratPernyataan').val(),
				fileSuratPernyataanId:$('#Edit-RekeningBank-FileSuratPernyataanId').val(),
				jenisMataUang:$('#Edit-RekeningBank-JenisMataUang').val(),
				kantorCabang:$('#Edit-RekeningBank-KantorCabang').val(),
				namaBank:$('#Edit-RekeningBank-NamaBank').val(),
				negara:$('#Edit-RekeningBank-Negara').val(),
				noRekening:$('#Edit-RekeningBank-NoRekening').val(),
				noRekeningFormat:$('#Edit-RekeningBank-NoRekeningFormat').val(),
				pemegangRekening:$('#Edit-RekeningBank-PemegangRekening').val(),
				vendorId:$('#Edit-RekeningBank-VendorId').val(),

            }
            RequestData('PUT', `/v1/RekeningBank/edit/${$('#md-RekeningBank-edit').data('id')}`, '#md-RekeningBank-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-RekeningBank-edit').modal('hide');
                    getListRekeningBank();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
