$(document).ready(function () {
    $('#SusunanPengurus-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-SusunanPengurus-add').modal('show');
        $('#SusunanPengurus-create_btn').unbind();
        $('#SusunanPengurus-create_btn').on('click', function () {
            addSusunanPengurusSave();
        });
    });
});

function addSusunanPengurusSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				email:$('#Add-SusunanPengurus-Email').val(),
				fileKtpKitas:$('#Add-SusunanPengurus-FileKtpKitas').val(),
				fileKtpKitasId:$('#Add-SusunanPengurus-FileKtpKitasId').val(),
				fileTandaTangan:$('#Add-SusunanPengurus-FileTandaTangan').val(),
				fileTandaTanganId:$('#Add-SusunanPengurus-FileTandaTanganId').val(),
				jabatan:$('#Add-SusunanPengurus-Jabatan').val(),
				nama:$('#Add-SusunanPengurus-Nama').val(),
				noKtpKitas:$('#Add-SusunanPengurus-NoKtpKitas').val(),
				tipePengurus:$('#Add-SusunanPengurus-TipePengurus').val(),
				vendorId:$('#Add-SusunanPengurus-VendorId').val(),

            }
            RequestData('POST', `/v1/SusunanPengurus/add`, '#md-SusunanPengurus-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-SusunanPengurus-add').modal('hide');
                    getListSusunanPengurus();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
