$(document).ready(function () {
    $('#SusunanSaham-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-SusunanSaham-add').modal('show');
        $('#SusunanSaham-create_btn').unbind();
        $('#SusunanSaham-create_btn').on('click', function () {
            addSusunanSahamSave();
        });
    });
});

function addSusunanSahamSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				badanUsaha:$('#Add-SusunanSaham-BadanUsaha').val(),
				docNpwp:$('#Add-SusunanSaham-DocNpwp').val(),
				email:$('#Add-SusunanSaham-Email').val(),
				jumlahSaham:$('#Add-SusunanSaham-JumlahSaham').val(),
				nama:$('#Add-SusunanSaham-Nama').val(),
				noKtpKitas:$('#Add-SusunanSaham-NoKtpKitas').val(),
				perorangan:$('#Add-SusunanSaham-Perorangan').is(":checked"),
				vendorId:$('#Add-SusunanSaham-VendorId').val(),
				wargaNegara:$('#Add-SusunanSaham-WargaNegara').val(),

            }
            RequestData('POST', `/v1/SusunanSaham/add`, '#md-SusunanSaham-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-SusunanSaham-add').modal('hide');
                    getListSusunanSaham();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
