function editSusunanSahamDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-SusunanSaham-edit').modal('show');

	$('#Edit-SusunanSaham-BadanUsaha').val(data.badanUsaha);
	$('#Edit-SusunanSaham-DocNpwp').val(data.docNpwp);
	$('#Edit-SusunanSaham-Email').val(data.email);
	$('#Edit-SusunanSaham-JumlahSaham').val(data.jumlahSaham);
	$('#Edit-SusunanSaham-Nama').val(data.nama);
	$('#Edit-SusunanSaham-NoKtpKitas').val(data.noKtpKitas);
	$('#Edit-SusunanSaham-Perorangan').prop('checked', data.perorangan);
	$('#Edit-SusunanSaham-VendorId').val(data.vendorId);
	$('#Edit-SusunanSaham-WargaNegara').val(data.wargaNegara);


    $('#md-SusunanSaham-edit').data('id', data.id);
    $('#SusunanSaham-edit_btn').unbind();
    $('#SusunanSaham-edit_btn').on('click', function () {
        editSusunanSahamSave();
    });
}

function editSusunanSahamSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				badanUsaha:$('#Edit-SusunanSaham-BadanUsaha').val(),
				docNpwp:$('#Edit-SusunanSaham-DocNpwp').val(),
				email:$('#Edit-SusunanSaham-Email').val(),
				jumlahSaham:$('#Edit-SusunanSaham-JumlahSaham').val(),
				nama:$('#Edit-SusunanSaham-Nama').val(),
				noKtpKitas:$('#Edit-SusunanSaham-NoKtpKitas').val(),
				perorangan:$('#Edit-SusunanSaham-Perorangan').is(":checked"),
				vendorId:$('#Edit-SusunanSaham-VendorId').val(),
				wargaNegara:$('#Edit-SusunanSaham-WargaNegara').val(),

            }
            RequestData('PUT', `/v1/SusunanSaham/edit/${$('#md-SusunanSaham-edit').data('id')}`, '#md-SusunanSaham-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-SusunanSaham-edit').modal('hide');
                    getListSusunanSaham();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
