$(document).ready(function () {
    $('#UserActivity-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-UserActivity-add').modal('show');
        $('#UserActivity-create_btn').unbind();
        $('#UserActivity-create_btn').on('click', function () {
            addUserActivitySave();
        });
    });
});

function addUserActivitySave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idUser:$('#Add-UserActivity-IdUser').val(),
				pageName:$('#Add-UserActivity-PageName').val(),

            }
            RequestData('POST', `/v1/UserActivity/add`, '#md-UserActivity-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-UserActivity-add').modal('hide');
                    getListUserActivity();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
