$(document).ready(function () {
    $('#UserDelegate-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-UserDelegate-add').modal('show');
        $('#UserDelegate-create_btn').unbind();
        $('#UserDelegate-create_btn').on('click', function () {
            addUserDelegateSave();
        });
    });
});

function addUserDelegateSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				expiredDate:$('#Add-UserDelegate-ExpiredDate').val(),
				idUser:$('#Add-UserDelegate-IdUser').val(),
				idUserDelegate:$('#Add-UserDelegate-IdUserDelegate').val(),
				startDate:$('#Add-UserDelegate-StartDate').val(),

            }
            RequestData('POST', `/v1/UserDelegate/add`, '#md-UserDelegate-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-UserDelegate-add').modal('hide');
                    getListUserDelegate();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
