function editUserDelegateDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-UserDelegate-edit').modal('show');

	$('#Edit-UserDelegate-ExpiredDate').val(data.expiredDate);
	$('#Edit-UserDelegate-IdUser').val(data.idUser);
	$('#Edit-UserDelegate-IdUserDelegate').val(data.idUserDelegate);
	$('#Edit-UserDelegate-StartDate').val(data.startDate);


    $('#md-UserDelegate-edit').data('id', data.id);
    $('#UserDelegate-edit_btn').unbind();
    $('#UserDelegate-edit_btn').on('click', function () {
        editUserDelegateSave();
    });
}

function editUserDelegateSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				expiredDate:$('#Edit-UserDelegate-ExpiredDate').val(),
				idUser:$('#Edit-UserDelegate-IdUser').val(),
				idUserDelegate:$('#Edit-UserDelegate-IdUserDelegate').val(),
				startDate:$('#Edit-UserDelegate-StartDate').val(),

            }
            RequestData('PUT', `/v1/UserDelegate/edit/${$('#md-UserDelegate-edit').data('id')}`, '#md-UserDelegate-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-UserDelegate-edit').modal('hide');
                    getListUserDelegate();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
