$(document).ready(function () {
    $('#UserPassword-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-UserPassword-add').modal('show');
        $('#UserPassword-create_btn').unbind();
        $('#UserPassword-create_btn').on('click', function () {
            addUserPasswordSave();
        });
    });
});

function addUserPasswordSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idUser:$('#Add-UserPassword-IdUser').val(),
				password:$('#Add-UserPassword-Password').val(),

            }
            RequestData('POST', `/v1/UserPassword/add`, '#md-UserPassword-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-UserPassword-add').modal('hide');
                    getListUserPassword();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
