function editUserPasswordDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-UserPassword-edit').modal('show');

	$('#Edit-UserPassword-IdUser').val(data.idUser);
	$('#Edit-UserPassword-Password').val(data.password);


    $('#md-UserPassword-edit').data('id', data.id);
    $('#UserPassword-edit_btn').unbind();
    $('#UserPassword-edit_btn').on('click', function () {
        editUserPasswordSave();
    });
}

function editUserPasswordSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idUser:$('#Edit-UserPassword-IdUser').val(),
				password:$('#Edit-UserPassword-Password').val(),

            }
            RequestData('PUT', `/v1/UserPassword/edit/${$('#md-UserPassword-edit').data('id')}`, '#md-UserPassword-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-UserPassword-edit').modal('hide');
                    getListUserPassword();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
