$(document).ready(function () {
    $('#UserRole-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-UserRole-add').modal('show');
        $('#UserRole-create_btn').unbind();
        $('#UserRole-create_btn').on('click', function () {
            addUserRoleSave();
        });
    });
});

function addUserRoleSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				idRole:$('#Add-UserRole-IdRole').val(),
				idUser:$('#Add-UserRole-IdUser').val(),

            }
            RequestData('POST', `/v1/UserRole/add`, '#md-UserRole-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-UserRole-add').modal('hide');
                    getListUserRole();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
