function editUserRoleDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-UserRole-edit').modal('show');

	$('#Edit-UserRole-IdRole').val(data.idRole);
	$('#Edit-UserRole-IdUser').val(data.idUser);


    $('#md-UserRole-edit').data('id', data.id);
    $('#UserRole-edit_btn').unbind();
    $('#UserRole-edit_btn').on('click', function () {
        editUserRoleSave();
    });
}

function editUserRoleSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				idRole:$('#Edit-UserRole-IdRole').val(),
				idUser:$('#Edit-UserRole-IdUser').val(),

            }
            RequestData('PUT', `/v1/UserRole/edit/${$('#md-UserRole-edit').data('id')}`, '#md-UserRole-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-UserRole-edit').modal('hide');
                    getListUserRole();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
