$(document).ready(function () {
    $('#VAnnouncement-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VAnnouncement-add').modal('show');
        $('#VAnnouncement-create_btn').unbind();
        $('#VAnnouncement-create_btn').on('click', function () {
            addVAnnouncementSave();
        });
    });
});

function addVAnnouncementSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				announcementCategory:$('#Add-VAnnouncement-AnnouncementCategory').val(),
				announcementType:$('#Add-VAnnouncement-AnnouncementType').val(),
				attachment:$('#Add-VAnnouncement-Attachment').val(),
				bidangUsaha:$('#Add-VAnnouncement-BidangUsaha').val(),
				civdId:$('#Add-VAnnouncement-CivdId').val(),
				description:$('#Add-VAnnouncement-Description').val(),
				endDate:$('#Add-VAnnouncement-EndDate').val(),
				golonganUsaha:$('#Add-VAnnouncement-GolonganUsaha').val(),
				k3sId:$('#Add-VAnnouncement-K3sId').val(),
				k3sName:$('#Add-VAnnouncement-K3sName').val(),
				publishDate:$('#Add-VAnnouncement-PublishDate').val(),
				tenderType:$('#Add-VAnnouncement-TenderType').val(),
				title:$('#Add-VAnnouncement-Title').val(),

            }
            RequestData('POST', `/v1/VAnnouncement/add`, '#md-VAnnouncement-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VAnnouncement-add').modal('hide');
                    getListVAnnouncement();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
