function editVBranchDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VBranch-edit').modal('show');

	$('#Edit-VBranch-Address').val(data.address);
	$('#Edit-VBranch-CivdId').val(data.civdId);
	$('#Edit-VBranch-CompanyType').val(data.companyType);
	$('#Edit-VBranch-CompletedDate').val(data.completedDate);
	$('#Edit-VBranch-ContactPerson').val(data.contactPerson);
	$('#Edit-VBranch-FaxNumber').val(data.faxNumber);
	$('#Edit-VBranch-IdVendor').val(data.idVendor);
	$('#Edit-VBranch-Npwp').val(data.npwp);
	$('#Edit-VBranch-PhoneNumber').val(data.phoneNumber);
	$('#Edit-VBranch-Pkp').val(data.pkp);
	$('#Edit-VBranch-Situ').val(data.situ);
	$('#Edit-VBranch-VendorBranchName').val(data.vendorBranchName);
	$('#Edit-VBranch-VendorEmail1').val(data.vendorEmail1);
	$('#Edit-VBranch-VendorEmail2').val(data.vendorEmail2);
	$('#Edit-VBranch-ZipCode').val(data.zipCode);


    $('#md-VBranch-edit').data('id', data.id);
    $('#VBranch-edit_btn').unbind();
    $('#VBranch-edit_btn').on('click', function () {
        editVBranchSave();
    });
}

function editVBranchSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				address:$('#Edit-VBranch-Address').val(),
				civdId:$('#Edit-VBranch-CivdId').val(),
				companyType:$('#Edit-VBranch-CompanyType').val(),
				completedDate:$('#Edit-VBranch-CompletedDate').val(),
				contactPerson:$('#Edit-VBranch-ContactPerson').val(),
				faxNumber:$('#Edit-VBranch-FaxNumber').val(),
				idVendor:$('#Edit-VBranch-IdVendor').val(),
				npwp:$('#Edit-VBranch-Npwp').val(),
				phoneNumber:$('#Edit-VBranch-PhoneNumber').val(),
				pkp:$('#Edit-VBranch-Pkp').val(),
				situ:$('#Edit-VBranch-Situ').val(),
				vendorBranchName:$('#Edit-VBranch-VendorBranchName').val(),
				vendorEmail1:$('#Edit-VBranch-VendorEmail1').val(),
				vendorEmail2:$('#Edit-VBranch-VendorEmail2').val(),
				zipCode:$('#Edit-VBranch-ZipCode').val(),

            }
            RequestData('PUT', `/v1/VBranch/edit/${$('#md-VBranch-edit').data('id')}`, '#md-VBranch-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VBranch-edit').modal('hide');
                    getListVBranch();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
