$(document).ready(function () {
    $('#VIzinUsaha-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VIzinUsaha-add').modal('show');
        $('#VIzinUsaha-create_btn').unbind();
        $('#VIzinUsaha-create_btn').on('click', function () {
            addVIzinUsahaSave();
        });
    });
});

function addVIzinUsahaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				akhirBerlaku:$('#Add-VIzinUsaha-AkhirBerlaku').val(),
				bidangUsaha:$('#Add-VIzinUsaha-BidangUsaha').val(),
				bidangUsahaCode:$('#Add-VIzinUsaha-BidangUsahaCode').val(),
				civdId:$('#Add-VIzinUsaha-CivdId').val(),
				completedDate:$('#Add-VIzinUsaha-CompletedDate').val(),
				fileIzinUsaha:$('#Add-VIzinUsaha-FileIzinUsaha').val(),
				fileIzinUsahaId:$('#Add-VIzinUsaha-FileIzinUsahaId').val(),
				golonganUsaha:$('#Add-VIzinUsaha-GolonganUsaha').val(),
				idVendor:$('#Add-VIzinUsaha-IdVendor').val(),
				instansiPemberiIzin:$('#Add-VIzinUsaha-InstansiPemberiIzin').val(),
				jenisIzinUsaha:$('#Add-VIzinUsaha-JenisIzinUsaha').val(),
				jenisMataUang:$('#Add-VIzinUsaha-JenisMataUang').val(),
				kekayaanBershi:$('#Add-VIzinUsaha-KekayaanBershi').val(),
				merkStp:$('#Add-VIzinUsaha-MerkStp').val(),
				mulaiBerlaku:$('#Add-VIzinUsaha-MulaiBerlaku').val(),
				noIzinUsaha:$('#Add-VIzinUsaha-NoIzinUsaha').val(),
				other:$('#Add-VIzinUsaha-Other').val(),
				peringkatInspeksi:$('#Add-VIzinUsaha-PeringkatInspeksi').val(),
				tipeStp:$('#Add-VIzinUsaha-TipeStp').val(),

            }
            RequestData('POST', `/v1/VIzinUsaha/add`, '#md-VIzinUsaha-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VIzinUsaha-add').modal('hide');
                    getListVIzinUsaha();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
