$(document).ready(function () {
    $('#VKompetensi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VKompetensi-add').modal('show');
        $('#VKompetensi-create_btn').unbind();
        $('#VKompetensi-create_btn').on('click', function () {
            addVKompetensiSave();
        });
    });
});

function addVKompetensiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				bidangSubBidang:$('#Add-VKompetensi-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Add-VKompetensi-BidangSubBidangCode').val(),
				civdId:$('#Add-VKompetensi-CivdId').val(),
				completedDate:$('#Add-VKompetensi-CompletedDate').val(),
				deskripsi:$('#Add-VKompetensi-Deskripsi').val(),
				document:$('#Add-VKompetensi-Document').val(),
				documentId:$('#Add-VKompetensi-DocumentId').val(),
				idVendor:$('#Add-VKompetensi-IdVendor').val(),
				jenisMataUang:$('#Add-VKompetensi-JenisMataUang').val(),
				nilaiKontrakPoso:$('#Add-VKompetensi-NilaiKontrakPoso').val(),
				noKontrakPoso:$('#Add-VKompetensi-NoKontrakPoso').val(),
				perusahaan:$('#Add-VKompetensi-Perusahaan').val(),
				progressKontrakPoso:$('#Add-VKompetensi-ProgressKontrakPoso').val(),
				tglKontrakPoso:$('#Add-VKompetensi-TglKontrakPoso').val(),
				tglPenyelesaian:$('#Add-VKompetensi-TglPenyelesaian').val(),

            }
            RequestData('POST', `/v1/VKompetensi/add`, '#md-VKompetensi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VKompetensi-add').modal('hide');
                    getListVKompetensi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
