	$('#Edit-VKompetensi-IdVendor').val(data.idVendor);
	$('#Edit-VKompetensi-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-VKompetensi-NilaiKontrakPoso').val(data.nilaiKontrakPoso);
	$('#Edit-VKompetensi-NoKontrakPoso').val(data.noKontrakPoso);
	$('#Edit-VKompetensi-Perusahaan').val(data.perusahaan);
	$('#Edit-VKompetensi-ProgressKontrakPoso').val(data.progressKontrakPoso);
	$('#Edit-VKompetensi-TglKontrakPoso').val(data.tglKontrakPoso);
	$('#Edit-VKompetensi-TglPenyelesaian').val(data.tglPenyelesaian);


