function editVKompetensiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				bidangSubBidang:$('#Edit-VKompetensi-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Edit-VKompetensi-BidangSubBidangCode').val(),
				civdId:$('#Edit-VKompetensi-CivdId').val(),
				completedDate:$('#Edit-VKompetensi-CompletedDate').val(),
				deskripsi:$('#Edit-VKompetensi-Deskripsi').val(),
				document:$('#Edit-VKompetensi-Document').val(),
				documentId:$('#Edit-VKompetensi-DocumentId').val(),
				idVendor:$('#Edit-VKompetensi-IdVendor').val(),
				jenisMataUang:$('#Edit-VKompetensi-JenisMataUang').val(),
				nilaiKontrakPoso:$('#Edit-VKompetensi-NilaiKontrakPoso').val(),
				noKontrakPoso:$('#Edit-VKompetensi-NoKontrakPoso').val(),
				perusahaan:$('#Edit-VKompetensi-Perusahaan').val(),
				progressKontrakPoso:$('#Edit-VKompetensi-ProgressKontrakPoso').val(),
				tglKontrakPoso:$('#Edit-VKompetensi-TglKontrakPoso').val(),
				tglPenyelesaian:$('#Edit-VKompetensi-TglPenyelesaian').val(),

            }
            RequestData('PUT', `/v1/VKompetensi/edit/${$('#md-VKompetensi-edit').data('id')}`, '#md-VKompetensi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VKompetensi-edit').modal('hide');
                    getListVKompetensi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
