$(document).ready(function () {
    $('#VLandasanHukum-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VLandasanHukum-add').modal('show');
        $('#VLandasanHukum-create_btn').unbind();
        $('#VLandasanHukum-create_btn').on('click', function () {
            addVLandasanHukumSave();
        });
    });
});

function addVLandasanHukumSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VLandasanHukum-CivdId').val(),
				completedDate:$('#Add-VLandasanHukum-CompletedDate').val(),
				fileLandasanHukum:$('#Add-VLandasanHukum-FileLandasanHukum').val(),
				fileLandasanHukumId:$('#Add-VLandasanHukum-FileLandasanHukumId').val(),
				idVendor:$('#Add-VLandasanHukum-IdVendor').val(),
				jenisAkta:$('#Add-VLandasanHukum-JenisAkta').val(),
				namaNotaris:$('#Add-VLandasanHukum-NamaNotaris').val(),
				namaSkMenteri:$('#Add-VLandasanHukum-NamaSkMenteri').val(),
				noAkta:$('#Add-VLandasanHukum-NoAkta').val(),
				tglAkta:$('#Add-VLandasanHukum-TglAkta').val(),

            }
            RequestData('POST', `/v1/VLandasanHukum/add`, '#md-VLandasanHukum-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VLandasanHukum-add').modal('hide');
                    getListVLandasanHukum();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
