function editVLandasanHukumDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VLandasanHukum-edit').modal('show');

	$('#Edit-VLandasanHukum-CivdId').val(data.civdId);
	$('#Edit-VLandasanHukum-CompletedDate').val(data.completedDate);
	$('#Edit-VLandasanHukum-FileLandasanHukum').val(data.fileLandasanHukum);
	$('#Edit-VLandasanHukum-FileLandasanHukumId').val(data.fileLandasanHukumId);
	$('#Edit-VLandasanHukum-IdVendor').val(data.idVendor);
	$('#Edit-VLandasanHukum-JenisAkta').val(data.jenisAkta);
	$('#Edit-VLandasanHukum-NamaNotaris').val(data.namaNotaris);
	$('#Edit-VLandasanHukum-NamaSkMenteri').val(data.namaSkMenteri);
	$('#Edit-VLandasanHukum-NoAkta').val(data.noAkta);
	$('#Edit-VLandasanHukum-TglAkta').val(data.tglAkta);


    $('#md-VLandasanHukum-edit').data('id', data.id);
    $('#VLandasanHukum-edit_btn').unbind();
    $('#VLandasanHukum-edit_btn').on('click', function () {
        editVLandasanHukumSave();
    });
}

function editVLandasanHukumSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VLandasanHukum-CivdId').val(),
				completedDate:$('#Edit-VLandasanHukum-CompletedDate').val(),
				fileLandasanHukum:$('#Edit-VLandasanHukum-FileLandasanHukum').val(),
				fileLandasanHukumId:$('#Edit-VLandasanHukum-FileLandasanHukumId').val(),
				idVendor:$('#Edit-VLandasanHukum-IdVendor').val(),
				jenisAkta:$('#Edit-VLandasanHukum-JenisAkta').val(),
				namaNotaris:$('#Edit-VLandasanHukum-NamaNotaris').val(),
				namaSkMenteri:$('#Edit-VLandasanHukum-NamaSkMenteri').val(),
				noAkta:$('#Edit-VLandasanHukum-NoAkta').val(),
				tglAkta:$('#Edit-VLandasanHukum-TglAkta').val(),

            }
            RequestData('PUT', `/v1/VLandasanHukum/edit/${$('#md-VLandasanHukum-edit').data('id')}`, '#md-VLandasanHukum-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VLandasanHukum-edit').modal('hide');
                    getListVLandasanHukum();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
