$(document).ready(function () {
    $('#VNeraca-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VNeraca-add').modal('show');
        $('#VNeraca-create_btn').unbind();
        $('#VNeraca-create_btn').on('click', function () {
            addVNeracaSave();
        });
    });
});

function addVNeracaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				accountReceivables:$('#Add-VNeraca-AccountReceivables').val(),
				akhirBerlaku:$('#Add-VNeraca-AkhirBerlaku').val(),
				cash:$('#Add-VNeraca-Cash').val(),
				civdId:$('#Add-VNeraca-CivdId').val(),
				completedDate:$('#Add-VNeraca-CompletedDate').val(),
				costOfRevenue:$('#Add-VNeraca-CostOfRevenue').val(),
				currentAsset:$('#Add-VNeraca-CurrentAsset').val(),
				currentLiabilities:$('#Add-VNeraca-CurrentLiabilities').val(),
				depreciationExpense:$('#Add-VNeraca-DepreciationExpense').val(),
				earningBeforeTax:$('#Add-VNeraca-EarningBeforeTax').val(),
				ebit:$('#Add-VNeraca-Ebit').val(),
				fileNeraca:$('#Add-VNeraca-FileNeraca').val(),
				fileNeracaId:$('#Add-VNeraca-FileNeracaId').val(),
				fixedAsset:$('#Add-VNeraca-FixedAsset').val(),
				golonganPerusahaan:$('#Add-VNeraca-GolonganPerusahaan').val(),
				grossProfit:$('#Add-VNeraca-GrossProfit').val(),
				idVendor:$('#Add-VNeraca-IdVendor').val(),
				interestExpense:$('#Add-VNeraca-InterestExpense').val(),
				jenisMataUang:$('#Add-VNeraca-JenisMataUang').val(),
				jenisMataUangSales:$('#Add-VNeraca-JenisMataUangSales').val(),
				jumlahAktiva:$('#Add-VNeraca-JumlahAktiva').val(),
				jumlahHutang:$('#Add-VNeraca-JumlahHutang').val(),
				kekayaanBersih:$('#Add-VNeraca-KekayaanBersih').val(),
				netEkuitas:$('#Add-VNeraca-NetEkuitas').val(),
				netProfit:$('#Add-VNeraca-NetProfit').val(),
				nonCurrentLiabilities:$('#Add-VNeraca-NonCurrentLiabilities').val(),
				operatingExpense:$('#Add-VNeraca-OperatingExpense').val(),
				otherCurrentAsset:$('#Add-VNeraca-OtherCurrentAsset').val(),
				othersExpense:$('#Add-VNeraca-OthersExpense').val(),
				othersIncome:$('#Add-VNeraca-OthersIncome').val(),
				penjualan:$('#Add-VNeraca-Penjualan').val(),
				periodeAkhir:$('#Add-VNeraca-PeriodeAkhir').val(),
				periodeAwal:$('#Add-VNeraca-PeriodeAwal').val(),
				statusAudit:$('#Add-VNeraca-StatusAudit').val(),
				tahun:$('#Add-VNeraca-Tahun').val(),
				tanahBangunan:$('#Add-VNeraca-TanahBangunan').val(),
				tglAkhirSales:$('#Add-VNeraca-TglAkhirSales').val(),
				tglSales:$('#Add-VNeraca-TglSales').val(),
				zeroControl:$('#Add-VNeraca-ZeroControl').val(),

            }
            RequestData('POST', `/v1/VNeraca/add`, '#md-VNeraca-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VNeraca-add').modal('hide');
                    getListVNeraca();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
