$(document).ready(function () {
    $('#VPajak-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VPajak-add').modal('show');
        $('#VPajak-create_btn').unbind();
        $('#VPajak-create_btn').on('click', function () {
            addVPajakSave();
        });
    });
});

function addVPajakSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VPajak-CivdId').val(),
				completedDate:$('#Add-VPajak-CompletedDate').val(),
				fileDokumen:$('#Add-VPajak-FileDokumen').val(),
				fileDokumenId:$('#Add-VPajak-FileDokumenId').val(),
				idVendor:$('#Add-VPajak-IdVendor').val(),
				kondisi:$('#Add-VPajak-Kondisi').val(),
				noDokumen:$('#Add-VPajak-NoDokumen').val(),
				periodeAkhir:$('#Add-VPajak-PeriodeAkhir').val(),
				periodeAwal:$('#Add-VPajak-PeriodeAwal').val(),
				tahun:$('#Add-VPajak-Tahun').val(),
				tanggal:$('#Add-VPajak-Tanggal').val(),
				tanggalAkhir:$('#Add-VPajak-TanggalAkhir').val(),
				tipeDokumen:$('#Add-VPajak-TipeDokumen').val(),

            }
            RequestData('POST', `/v1/VPajak/add`, '#md-VPajak-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VPajak-add').modal('hide');
                    getListVPajak();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
