$(document).ready(function () {
    $('#VPengalaman-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VPengalaman-add').modal('show');
        $('#VPengalaman-create_btn').unbind();
        $('#VPengalaman-create_btn').on('click', function () {
            addVPengalamanSave();
        });
    });
});

function addVPengalamanSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				alamat:$('#Add-VPengalaman-Alamat').val(),
				bidangSubBidang:$('#Add-VPengalaman-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Add-VPengalaman-BidangSubBidangCode').val(),
				civdId:$('#Add-VPengalaman-CivdId').val(),
				completedDate:$('#Add-VPengalaman-CompletedDate').val(),
				fileBast:$('#Add-VPengalaman-FileBast').val(),
				fileBastId:$('#Add-VPengalaman-FileBastId').val(),
				fileBuktiPengalaman:$('#Add-VPengalaman-FileBuktiPengalaman').val(),
				fileBuktiPengalamanId:$('#Add-VPengalaman-FileBuktiPengalamanId').val(),
				idVendor:$('#Add-VPengalaman-IdVendor').val(),
				jenisMataUang:$('#Add-VPengalaman-JenisMataUang').val(),
				lokasi:$('#Add-VPengalaman-Lokasi').val(),
				namaPaketPekerjaan:$('#Add-VPengalaman-NamaPaketPekerjaan').val(),
				namaPenggunaJasa:$('#Add-VPengalaman-NamaPenggunaJasa').val(),
				nilaiKontrakPo:$('#Add-VPengalaman-NilaiKontrakPo').val(),
				noKontrakPo:$('#Add-VPengalaman-NoKontrakPo').val(),
				noTelepon:$('#Add-VPengalaman-NoTelepon').val(),
				selesaiKontrakPo:$('#Add-VPengalaman-SelesaiKontrakPo').val(),
				tglKontrakPo:$('#Add-VPengalaman-TglKontrakPo').val(),

            }
            RequestData('POST', `/v1/VPengalaman/add`, '#md-VPengalaman-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VPengalaman-add').modal('hide');
                    getListVPengalaman();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
