	$('#Edit-VPengalaman-CompletedDate').val(data.completedDate);
	$('#Edit-VPengalaman-FileBast').val(data.fileBast);
	$('#Edit-VPengalaman-FileBastId').val(data.fileBastId);
	$('#Edit-VPengalaman-FileBuktiPengalaman').val(data.fileBuktiPengalaman);
	$('#Edit-VPengalaman-FileBuktiPengalamanId').val(data.fileBuktiPengalamanId);
	$('#Edit-VPengalaman-IdVendor').val(data.idVendor);
	$('#Edit-VPengalaman-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-VPengalaman-Lokasi').val(data.lokasi);
	$('#Edit-VPengalaman-NamaPaketPekerjaan').val(data.namaPaketPekerjaan);
	$('#Edit-VPengalaman-NamaPenggunaJasa').val(data.namaPenggunaJasa);
	$('#Edit-VPengalaman-NilaiKontrakPo').val(data.nilaiKontrakPo);
	$('#Edit-VPengalaman-NoKontrakPo').val(data.noKontrakPo);
	$('#Edit-VPengalaman-NoTelepon').val(data.noTelepon);
