function editVRekeningBankDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VRekeningBank-edit').modal('show');

	$('#Edit-VRekeningBank-CivdId').val(data.civdId);
	$('#Edit-VRekeningBank-CompletedDate').val(data.completedDate);
	$('#Edit-VRekeningBank-FileSuratPernyataan').val(data.fileSuratPernyataan);
	$('#Edit-VRekeningBank-FileSuratPernyataanId').val(data.fileSuratPernyataanId);
	$('#Edit-VRekeningBank-IdVendor').val(data.idVendor);
	$('#Edit-VRekeningBank-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-VRekeningBank-KantorCabang').val(data.kantorCabang);
	$('#Edit-VRekeningBank-NamaBank').val(data.namaBank);
	$('#Edit-VRekeningBank-Negara').val(data.negara);
	$('#Edit-VRekeningBank-NoRekening').val(data.noRekening);
	$('#Edit-VRekeningBank-NoRekeningFormat').val(data.noRekeningFormat);
	$('#Edit-VRekeningBank-PemegangRekening').val(data.pemegangRekening);


    $('#md-VRekeningBank-edit').data('id', data.id);
    $('#VRekeningBank-edit_btn').unbind();
    $('#VRekeningBank-edit_btn').on('click', function () {
        editVRekeningBankSave();
    });
}

function editVRekeningBankSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VRekeningBank-CivdId').val(),
				completedDate:$('#Edit-VRekeningBank-CompletedDate').val(),
				fileSuratPernyataan:$('#Edit-VRekeningBank-FileSuratPernyataan').val(),
				fileSuratPernyataanId:$('#Edit-VRekeningBank-FileSuratPernyataanId').val(),
				idVendor:$('#Edit-VRekeningBank-IdVendor').val(),
				jenisMataUang:$('#Edit-VRekeningBank-JenisMataUang').val(),
				kantorCabang:$('#Edit-VRekeningBank-KantorCabang').val(),
				namaBank:$('#Edit-VRekeningBank-NamaBank').val(),
				negara:$('#Edit-VRekeningBank-Negara').val(),
				noRekening:$('#Edit-VRekeningBank-NoRekening').val(),
				noRekeningFormat:$('#Edit-VRekeningBank-NoRekeningFormat').val(),
				pemegangRekening:$('#Edit-VRekeningBank-PemegangRekening').val(),

            }
            RequestData('PUT', `/v1/VRekeningBank/edit/${$('#md-VRekeningBank-edit').data('id')}`, '#md-VRekeningBank-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VRekeningBank-edit').modal('hide');
                    getListVRekeningBank();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
