$(document).ready(function () {
    $('#VSanksi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VSanksi-add').modal('show');
        $('#VSanksi-create_btn').unbind();
        $('#VSanksi-create_btn').on('click', function () {
            addVSanksiSave();
        });
    });
});

function addVSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VSanksi-CivdId').val(),
				filePernyataanPerbaikan:$('#Add-VSanksi-FilePernyataanPerbaikan').val(),
				filePernyataanPerbaikanId:$('#Add-VSanksi-FilePernyataanPerbaikanId').val(),
				fileSuratSanksi:$('#Add-VSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Add-VSanksi-FileSuratSanksiId').val(),
				idVendor:$('#Add-VSanksi-IdVendor').val(),
				keterangan:$('#Add-VSanksi-Keterangan').val(),
				percobaan:$('#Add-VSanksi-Percobaan').val(),
				sanksi:$('#Add-VSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Add-VSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Add-VSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Add-VSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Add-VSanksi-TglPelepasanSanksi').val(),

            }
            RequestData('POST', `/v1/VSanksi/add`, '#md-VSanksi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VSanksi-add').modal('hide');
                    getListVSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
