function editVSanksiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VSanksi-edit').modal('show');

	$('#Edit-VSanksi-CivdId').val(data.civdId);
	$('#Edit-VSanksi-FilePernyataanPerbaikan').val(data.filePernyataanPerbaikan);
	$('#Edit-VSanksi-FilePernyataanPerbaikanId').val(data.filePernyataanPerbaikanId);
	$('#Edit-VSanksi-FileSuratSanksi').val(data.fileSuratSanksi);
	$('#Edit-VSanksi-FileSuratSanksiId').val(data.fileSuratSanksiId);
	$('#Edit-VSanksi-IdVendor').val(data.idVendor);
	$('#Edit-VSanksi-Keterangan').val(data.keterangan);
	$('#Edit-VSanksi-Percobaan').val(data.percobaan);
	$('#Edit-VSanksi-Sanksi').val(data.sanksi);
	$('#Edit-VSanksi-TglBerakhirPercobaan').val(data.tglBerakhirPercobaan);
	$('#Edit-VSanksi-TglBerakhirSanksi').val(data.tglBerakhirSanksi);
	$('#Edit-VSanksi-TglBerlakuSanksi').val(data.tglBerlakuSanksi);
	$('#Edit-VSanksi-TglPelepasanSanksi').val(data.tglPelepasanSanksi);


    $('#md-VSanksi-edit').data('id', data.id);
    $('#VSanksi-edit_btn').unbind();
    $('#VSanksi-edit_btn').on('click', function () {
        editVSanksiSave();
    });
}

function editVSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VSanksi-CivdId').val(),
				filePernyataanPerbaikan:$('#Edit-VSanksi-FilePernyataanPerbaikan').val(),
				filePernyataanPerbaikanId:$('#Edit-VSanksi-FilePernyataanPerbaikanId').val(),
				fileSuratSanksi:$('#Edit-VSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Edit-VSanksi-FileSuratSanksiId').val(),
				idVendor:$('#Edit-VSanksi-IdVendor').val(),
				keterangan:$('#Edit-VSanksi-Keterangan').val(),
				percobaan:$('#Edit-VSanksi-Percobaan').val(),
				sanksi:$('#Edit-VSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Edit-VSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Edit-VSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Edit-VSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Edit-VSanksi-TglPelepasanSanksi').val(),

            }
            RequestData('PUT', `/v1/VSanksi/edit/${$('#md-VSanksi-edit').data('id')}`, '#md-VSanksi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VSanksi-edit').modal('hide');
                    getListVSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
