$(document).ready(function () {
    $('#VSpda-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VSpda-add').modal('show');
        $('#VSpda-create_btn').unbind();
        $('#VSpda-create_btn').on('click', function () {
            addVSpdaSave();
        });
    });
});

function addVSpdaSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VSpda-CivdId').val(),
				expiredDate:$('#Add-VSpda-ExpiredDate').val(),
				fileSpda:$('#Add-VSpda-FileSpda').val(),
				fileSpdaId:$('#Add-VSpda-FileSpdaId').val(),
				idVendor:$('#Add-VSpda-IdVendor').val(),
				spdaNo:$('#Add-VSpda-SpdaNo').val(),
				spdaValidity:$('#Add-VSpda-SpdaValidity').val(),
				uploadDate:$('#Add-VSpda-UploadDate').val(),

            }
            RequestData('POST', `/v1/VSpda/add`, '#md-VSpda-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VSpda-add').modal('hide');
                    getListVSpda();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
