function editVSpdaDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VSpda-edit').modal('show');

	$('#Edit-VSpda-CivdId').val(data.civdId);
	$('#Edit-VSpda-ExpiredDate').val(data.expiredDate);
	$('#Edit-VSpda-FileSpda').val(data.fileSpda);
	$('#Edit-VSpda-FileSpdaId').val(data.fileSpdaId);
	$('#Edit-VSpda-IdVendor').val(data.idVendor);
	$('#Edit-VSpda-SpdaNo').val(data.spdaNo);
	$('#Edit-VSpda-SpdaValidity').val(data.spdaValidity);
	$('#Edit-VSpda-UploadDate').val(data.uploadDate);


    $('#md-VSpda-edit').data('id', data.id);
    $('#VSpda-edit_btn').unbind();
    $('#VSpda-edit_btn').on('click', function () {
        editVSpdaSave();
    });
}

function editVSpdaSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VSpda-CivdId').val(),
				expiredDate:$('#Edit-VSpda-ExpiredDate').val(),
				fileSpda:$('#Edit-VSpda-FileSpda').val(),
				fileSpdaId:$('#Edit-VSpda-FileSpdaId').val(),
				idVendor:$('#Edit-VSpda-IdVendor').val(),
				spdaNo:$('#Edit-VSpda-SpdaNo').val(),
				spdaValidity:$('#Edit-VSpda-SpdaValidity').val(),
				uploadDate:$('#Edit-VSpda-UploadDate').val(),

            }
            RequestData('PUT', `/v1/VSpda/edit/${$('#md-VSpda-edit').data('id')}`, '#md-VSpda-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VSpda-edit').modal('hide');
                    getListVSpda();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
