$(document).ready(function () {
    $('#VSusunanPengurus-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VSusunanPengurus-add').modal('show');
        $('#VSusunanPengurus-create_btn').unbind();
        $('#VSusunanPengurus-create_btn').on('click', function () {
            addVSusunanPengurusSave();
        });
    });
});

function addVSusunanPengurusSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VSusunanPengurus-CivdId').val(),
				completedDate:$('#Add-VSusunanPengurus-CompletedDate').val(),
				email:$('#Add-VSusunanPengurus-Email').val(),
				fileKtpKitas:$('#Add-VSusunanPengurus-FileKtpKitas').val(),
				fileKtpKitasId:$('#Add-VSusunanPengurus-FileKtpKitasId').val(),
				fileTandaTangan:$('#Add-VSusunanPengurus-FileTandaTangan').val(),
				fileTandaTanganId:$('#Add-VSusunanPengurus-FileTandaTanganId').val(),
				idVendor:$('#Add-VSusunanPengurus-IdVendor').val(),
				jabatan:$('#Add-VSusunanPengurus-Jabatan').val(),
				nama:$('#Add-VSusunanPengurus-Nama').val(),
				noKtpKitas:$('#Add-VSusunanPengurus-NoKtpKitas').val(),
				tipePengurus:$('#Add-VSusunanPengurus-TipePengurus').val(),

            }
            RequestData('POST', `/v1/VSusunanPengurus/add`, '#md-VSusunanPengurus-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VSusunanPengurus-add').modal('hide');
                    getListVSusunanPengurus();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
