function editVSusunanPengurusDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VSusunanPengurus-edit').modal('show');

	$('#Edit-VSusunanPengurus-CivdId').val(data.civdId);
	$('#Edit-VSusunanPengurus-CompletedDate').val(data.completedDate);
	$('#Edit-VSusunanPengurus-Email').val(data.email);
	$('#Edit-VSusunanPengurus-FileKtpKitas').val(data.fileKtpKitas);
	$('#Edit-VSusunanPengurus-FileKtpKitasId').val(data.fileKtpKitasId);
	$('#Edit-VSusunanPengurus-FileTandaTangan').val(data.fileTandaTangan);
	$('#Edit-VSusunanPengurus-FileTandaTanganId').val(data.fileTandaTanganId);
	$('#Edit-VSusunanPengurus-IdVendor').val(data.idVendor);
	$('#Edit-VSusunanPengurus-Jabatan').val(data.jabatan);
	$('#Edit-VSusunanPengurus-Nama').val(data.nama);
	$('#Edit-VSusunanPengurus-NoKtpKitas').val(data.noKtpKitas);
	$('#Edit-VSusunanPengurus-TipePengurus').val(data.tipePengurus);


    $('#md-VSusunanPengurus-edit').data('id', data.id);
    $('#VSusunanPengurus-edit_btn').unbind();
    $('#VSusunanPengurus-edit_btn').on('click', function () {
        editVSusunanPengurusSave();
    });
}

function editVSusunanPengurusSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VSusunanPengurus-CivdId').val(),
				completedDate:$('#Edit-VSusunanPengurus-CompletedDate').val(),
				email:$('#Edit-VSusunanPengurus-Email').val(),
				fileKtpKitas:$('#Edit-VSusunanPengurus-FileKtpKitas').val(),
				fileKtpKitasId:$('#Edit-VSusunanPengurus-FileKtpKitasId').val(),
				fileTandaTangan:$('#Edit-VSusunanPengurus-FileTandaTangan').val(),
				fileTandaTanganId:$('#Edit-VSusunanPengurus-FileTandaTanganId').val(),
				idVendor:$('#Edit-VSusunanPengurus-IdVendor').val(),
				jabatan:$('#Edit-VSusunanPengurus-Jabatan').val(),
				nama:$('#Edit-VSusunanPengurus-Nama').val(),
				noKtpKitas:$('#Edit-VSusunanPengurus-NoKtpKitas').val(),
				tipePengurus:$('#Edit-VSusunanPengurus-TipePengurus').val(),

            }
            RequestData('PUT', `/v1/VSusunanPengurus/edit/${$('#md-VSusunanPengurus-edit').data('id')}`, '#md-VSusunanPengurus-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VSusunanPengurus-edit').modal('hide');
                    getListVSusunanPengurus();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
