function editVSusunanSahamDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VSusunanSaham-edit').modal('show');

	$('#Edit-VSusunanSaham-BadanUsaha').val(data.badanUsaha);
	$('#Edit-VSusunanSaham-CivdId').val(data.civdId);
	$('#Edit-VSusunanSaham-CompletedDate').val(data.completedDate);
	$('#Edit-VSusunanSaham-DocNpwp').val(data.docNpwp);
	$('#Edit-VSusunanSaham-Email').val(data.email);
	$('#Edit-VSusunanSaham-IdVendor').val(data.idVendor);
	$('#Edit-VSusunanSaham-JumlahSaham').val(data.jumlahSaham);
	$('#Edit-VSusunanSaham-Nama').val(data.nama);
	$('#Edit-VSusunanSaham-NoKtpKitas').val(data.noKtpKitas);
	$('#Edit-VSusunanSaham-Perorangan').prop('checked', data.perorangan);
	$('#Edit-VSusunanSaham-WargaNegara').val(data.wargaNegara);


    $('#md-VSusunanSaham-edit').data('id', data.id);
    $('#VSusunanSaham-edit_btn').unbind();
    $('#VSusunanSaham-edit_btn').on('click', function () {
        editVSusunanSahamSave();
    });
}

function editVSusunanSahamSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				badanUsaha:$('#Edit-VSusunanSaham-BadanUsaha').val(),
				civdId:$('#Edit-VSusunanSaham-CivdId').val(),
				completedDate:$('#Edit-VSusunanSaham-CompletedDate').val(),
				docNpwp:$('#Edit-VSusunanSaham-DocNpwp').val(),
				email:$('#Edit-VSusunanSaham-Email').val(),
				idVendor:$('#Edit-VSusunanSaham-IdVendor').val(),
				jumlahSaham:$('#Edit-VSusunanSaham-JumlahSaham').val(),
				nama:$('#Edit-VSusunanSaham-Nama').val(),
				noKtpKitas:$('#Edit-VSusunanSaham-NoKtpKitas').val(),
				perorangan:$('#Edit-VSusunanSaham-Perorangan').is(":checked"),
				wargaNegara:$('#Edit-VSusunanSaham-WargaNegara').val(),

            }
            RequestData('PUT', `/v1/VSusunanSaham/edit/${$('#md-VSusunanSaham-edit').data('id')}`, '#md-VSusunanSaham-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VSusunanSaham-edit').modal('hide');
                    getListVSusunanSaham();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
