$(document).ready(function () {
    $('#VendorAfiliasi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorAfiliasi-add').modal('show');
        $('#VendorAfiliasi-create_btn').unbind();
        $('#VendorAfiliasi-create_btn').on('click', function () {
            addVendorAfiliasiSave();
        });
    });
});

function addVendorAfiliasiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VendorAfiliasi-CivdId').val(),
				completedDate:$('#Add-VendorAfiliasi-CompletedDate').val(),
				deskripsi:$('#Add-VendorAfiliasi-Deskripsi').val(),
				fileAfiliasiId:$('#Add-VendorAfiliasi-FileAfiliasiId').val(),
				idVendor:$('#Add-VendorAfiliasi-IdVendor').val(),
				share:$('#Add-VendorAfiliasi-Share').val(),
				terafiliasi:$('#Add-VendorAfiliasi-Terafiliasi').val(),
				tipeAfiliasi:$('#Add-VendorAfiliasi-TipeAfiliasi').val(),

            }
            RequestData('POST', `/v1/VendorAfiliasi/add`, '#md-VendorAfiliasi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorAfiliasi-add').modal('hide');
                    getListVendorAfiliasi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
