function editVendorAfiliasiDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VendorAfiliasi-edit').modal('show');

	$('#Edit-VendorAfiliasi-CivdId').val(data.civdId);
	$('#Edit-VendorAfiliasi-CompletedDate').val(data.completedDate);
	$('#Edit-VendorAfiliasi-Deskripsi').val(data.deskripsi);
	$('#Edit-VendorAfiliasi-FileAfiliasiId').val(data.fileAfiliasiId);
	$('#Edit-VendorAfiliasi-IdVendor').val(data.idVendor);
	$('#Edit-VendorAfiliasi-Share').val(data.share);
	$('#Edit-VendorAfiliasi-Terafiliasi').val(data.terafiliasi);
	$('#Edit-VendorAfiliasi-TipeAfiliasi').val(data.tipeAfiliasi);


    $('#md-VendorAfiliasi-edit').data('id', data.id);
    $('#VendorAfiliasi-edit_btn').unbind();
    $('#VendorAfiliasi-edit_btn').on('click', function () {
        editVendorAfiliasiSave();
    });
}

function editVendorAfiliasiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VendorAfiliasi-CivdId').val(),
				completedDate:$('#Edit-VendorAfiliasi-CompletedDate').val(),
				deskripsi:$('#Edit-VendorAfiliasi-Deskripsi').val(),
				fileAfiliasiId:$('#Edit-VendorAfiliasi-FileAfiliasiId').val(),
				idVendor:$('#Edit-VendorAfiliasi-IdVendor').val(),
				share:$('#Edit-VendorAfiliasi-Share').val(),
				terafiliasi:$('#Edit-VendorAfiliasi-Terafiliasi').val(),
				tipeAfiliasi:$('#Edit-VendorAfiliasi-TipeAfiliasi').val(),

            }
            RequestData('PUT', `/v1/VendorAfiliasi/edit/${$('#md-VendorAfiliasi-edit').data('id')}`, '#md-VendorAfiliasi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorAfiliasi-edit').modal('hide');
                    getListVendorAfiliasi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
