$(document).ready(function () {
    $('#VendorLandasanHukum-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorLandasanHukum-add').modal('show');
        $('#VendorLandasanHukum-create_btn').unbind();
        $('#VendorLandasanHukum-create_btn').on('click', function () {
            addVendorLandasanHukumSave();
        });
    });
});

function addVendorLandasanHukumSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VendorLandasanHukum-CivdId').val(),
				completedDate:$('#Add-VendorLandasanHukum-CompletedDate').val(),
				fileLandasanHukum:$('#Add-VendorLandasanHukum-FileLandasanHukum').val(),
				fileLandasanHukumId:$('#Add-VendorLandasanHukum-FileLandasanHukumId').val(),
				idVendor:$('#Add-VendorLandasanHukum-IdVendor').val(),
				jenisAkta:$('#Add-VendorLandasanHukum-JenisAkta').val(),
				namaNotaris:$('#Add-VendorLandasanHukum-NamaNotaris').val(),
				namaSkMenteri:$('#Add-VendorLandasanHukum-NamaSkMenteri').val(),
				noAkta:$('#Add-VendorLandasanHukum-NoAkta').val(),
				tglAkta:$('#Add-VendorLandasanHukum-TglAkta').val(),

            }
            RequestData('POST', `/v1/VendorLandasanHukum/add`, '#md-VendorLandasanHukum-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorLandasanHukum-add').modal('hide');
                    getListVendorLandasanHukum();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
