$(document).ready(function () {
    $('#VendorNeraca-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorNeraca-add').modal('show');
        $('#VendorNeraca-create_btn').unbind();
        $('#VendorNeraca-create_btn').on('click', function () {
            addVendorNeracaSave();
        });
    });
});

