				currentLiabilities:$('#Add-VendorNeraca-CurrentLiabilities').val(),
				depreciationExpense:$('#Add-VendorNeraca-DepreciationExpense').val(),
				earningBeforeTax:$('#Add-VendorNeraca-EarningBeforeTax').val(),
				ebit:$('#Add-VendorNeraca-Ebit').val(),
				fileNeraca:$('#Add-VendorNeraca-FileNeraca').val(),
