				jenisMataUang:$('#Add-VendorNeraca-JenisMataUang').val(),
				jenisMataUangSales:$('#Add-VendorNeraca-JenisMataUangSales').val(),
				jumlahAktiva:$('#Add-VendorNeraca-JumlahAktiva').val(),
				jumlahHutang:$('#Add-VendorNeraca-JumlahHutang').val(),
				kekayaanBersih:$('#Add-VendorNeraca-KekayaanBersih').val(),
				netEkuitas:$('#Add-VendorNeraca-NetEkuitas').val(),
				netProfit:$('#Add-VendorNeraca-NetProfit').val(),
				nonCurrentLiabilities:$('#Add-VendorNeraca-NonCurrentLiabilities').val(),
				operatingExpense:$('#Add-VendorNeraca-OperatingExpense').val(),
				otherCurrentAsset:$('#Add-VendorNeraca-OtherCurrentAsset').val(),
				othersExpense:$('#Add-VendorNeraca-OthersExpense').val(),
