$(document).ready(function () {
    $('#VendorPajak-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorPajak-add').modal('show');
        $('#VendorPajak-create_btn').unbind();
        $('#VendorPajak-create_btn').on('click', function () {
            addVendorPajakSave();
        });
    });
});

function addVendorPajakSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VendorPajak-CivdId').val(),
				completedDate:$('#Add-VendorPajak-CompletedDate').val(),
				fileDokumen:$('#Add-VendorPajak-FileDokumen').val(),
				fileDokumenId:$('#Add-VendorPajak-FileDokumenId').val(),
				idVendor:$('#Add-VendorPajak-IdVendor').val(),
				kondisi:$('#Add-VendorPajak-Kondisi').val(),
				noDokumen:$('#Add-VendorPajak-NoDokumen').val(),
				periodeAkhir:$('#Add-VendorPajak-PeriodeAkhir').val(),
				periodeAwal:$('#Add-VendorPajak-PeriodeAwal').val(),
				tahun:$('#Add-VendorPajak-Tahun').val(),
				tanggal:$('#Add-VendorPajak-Tanggal').val(),
				tanggalAkhir:$('#Add-VendorPajak-TanggalAkhir').val(),
				tipeDokumen:$('#Add-VendorPajak-TipeDokumen').val(),

            }
            RequestData('POST', `/v1/VendorPajak/add`, '#md-VendorPajak-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorPajak-add').modal('hide');
                    getListVendorPajak();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
