function editVendorPajakDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VendorPajak-edit').modal('show');

	$('#Edit-VendorPajak-CivdId').val(data.civdId);
	$('#Edit-VendorPajak-CompletedDate').val(data.completedDate);
	$('#Edit-VendorPajak-FileDokumen').val(data.fileDokumen);
	$('#Edit-VendorPajak-FileDokumenId').val(data.fileDokumenId);
	$('#Edit-VendorPajak-IdVendor').val(data.idVendor);
	$('#Edit-VendorPajak-Kondisi').val(data.kondisi);
	$('#Edit-VendorPajak-NoDokumen').val(data.noDokumen);
	$('#Edit-VendorPajak-PeriodeAkhir').val(data.periodeAkhir);
	$('#Edit-VendorPajak-PeriodeAwal').val(data.periodeAwal);
	$('#Edit-VendorPajak-Tahun').val(data.tahun);
	$('#Edit-VendorPajak-Tanggal').val(data.tanggal);
	$('#Edit-VendorPajak-TanggalAkhir').val(data.tanggalAkhir);
	$('#Edit-VendorPajak-TipeDokumen').val(data.tipeDokumen);


    $('#md-VendorPajak-edit').data('id', data.id);
    $('#VendorPajak-edit_btn').unbind();
    $('#VendorPajak-edit_btn').on('click', function () {
        editVendorPajakSave();
    });
}

function editVendorPajakSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VendorPajak-CivdId').val(),
				completedDate:$('#Edit-VendorPajak-CompletedDate').val(),
				fileDokumen:$('#Edit-VendorPajak-FileDokumen').val(),
				fileDokumenId:$('#Edit-VendorPajak-FileDokumenId').val(),
				idVendor:$('#Edit-VendorPajak-IdVendor').val(),
				kondisi:$('#Edit-VendorPajak-Kondisi').val(),
				noDokumen:$('#Edit-VendorPajak-NoDokumen').val(),
				periodeAkhir:$('#Edit-VendorPajak-PeriodeAkhir').val(),
				periodeAwal:$('#Edit-VendorPajak-PeriodeAwal').val(),
				tahun:$('#Edit-VendorPajak-Tahun').val(),
				tanggal:$('#Edit-VendorPajak-Tanggal').val(),
				tanggalAkhir:$('#Edit-VendorPajak-TanggalAkhir').val(),
				tipeDokumen:$('#Edit-VendorPajak-TipeDokumen').val(),

            }
            RequestData('PUT', `/v1/VendorPajak/edit/${$('#md-VendorPajak-edit').data('id')}`, '#md-VendorPajak-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorPajak-edit').modal('hide');
                    getListVendorPajak();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
