$(document).ready(function () {
    $('#VendorPengalaman-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorPengalaman-add').modal('show');
        $('#VendorPengalaman-create_btn').unbind();
        $('#VendorPengalaman-create_btn').on('click', function () {
            addVendorPengalamanSave();
        });
    });
});

function addVendorPengalamanSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				alamat:$('#Add-VendorPengalaman-Alamat').val(),
				bidangSubBidang:$('#Add-VendorPengalaman-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Add-VendorPengalaman-BidangSubBidangCode').val(),
				civdId:$('#Add-VendorPengalaman-CivdId').val(),
				completedDate:$('#Add-VendorPengalaman-CompletedDate').val(),
				fileBast:$('#Add-VendorPengalaman-FileBast').val(),
				fileBastId:$('#Add-VendorPengalaman-FileBastId').val(),
				fileBuktiPengalaman:$('#Add-VendorPengalaman-FileBuktiPengalaman').val(),
				fileBuktiPengalamanId:$('#Add-VendorPengalaman-FileBuktiPengalamanId').val(),
				idVendor:$('#Add-VendorPengalaman-IdVendor').val(),
				jenisMataUang:$('#Add-VendorPengalaman-JenisMataUang').val(),
				lokasi:$('#Add-VendorPengalaman-Lokasi').val(),
				namaPaketPekerjaan:$('#Add-VendorPengalaman-NamaPaketPekerjaan').val(),
				namaPenggunaJasa:$('#Add-VendorPengalaman-NamaPenggunaJasa').val(),
				nilaiKontrakPo:$('#Add-VendorPengalaman-NilaiKontrakPo').val(),
				noKontrakPo:$('#Add-VendorPengalaman-NoKontrakPo').val(),
				noTelepon:$('#Add-VendorPengalaman-NoTelepon').val(),
				selesaiKontrakPo:$('#Add-VendorPengalaman-SelesaiKontrakPo').val(),
				tglKontrakPo:$('#Add-VendorPengalaman-TglKontrakPo').val(),

            }
            RequestData('POST', `/v1/VendorPengalaman/add`, '#md-VendorPengalaman-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorPengalaman-add').modal('hide');
                    getListVendorPengalaman();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
