function editVendorPengalamanSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				alamat:$('#Edit-VendorPengalaman-Alamat').val(),
				bidangSubBidang:$('#Edit-VendorPengalaman-BidangSubBidang').val(),
				bidangSubBidangCode:$('#Edit-VendorPengalaman-BidangSubBidangCode').val(),
				civdId:$('#Edit-VendorPengalaman-CivdId').val(),
				completedDate:$('#Edit-VendorPengalaman-CompletedDate').val(),
				fileBast:$('#Edit-VendorPengalaman-FileBast').val(),
				fileBastId:$('#Edit-VendorPengalaman-FileBastId').val(),
				fileBuktiPengalaman:$('#Edit-VendorPengalaman-FileBuktiPengalaman').val(),
				fileBuktiPengalamanId:$('#Edit-VendorPengalaman-FileBuktiPengalamanId').val(),
				idVendor:$('#Edit-VendorPengalaman-IdVendor').val(),
				jenisMataUang:$('#Edit-VendorPengalaman-JenisMataUang').val(),
				lokasi:$('#Edit-VendorPengalaman-Lokasi').val(),
				namaPaketPekerjaan:$('#Edit-VendorPengalaman-NamaPaketPekerjaan').val(),
				namaPenggunaJasa:$('#Edit-VendorPengalaman-NamaPenggunaJasa').val(),
				nilaiKontrakPo:$('#Edit-VendorPengalaman-NilaiKontrakPo').val(),
				noKontrakPo:$('#Edit-VendorPengalaman-NoKontrakPo').val(),
				noTelepon:$('#Edit-VendorPengalaman-NoTelepon').val(),
				selesaiKontrakPo:$('#Edit-VendorPengalaman-SelesaiKontrakPo').val(),
				tglKontrakPo:$('#Edit-VendorPengalaman-TglKontrakPo').val(),

            }
            RequestData('PUT', `/v1/VendorPengalaman/edit/${$('#md-VendorPengalaman-edit').data('id')}`, '#md-VendorPengalaman-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorPengalaman-edit').modal('hide');
                    getListVendorPengalaman();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
