function editVendorRekeningBankDialog(el) {
    var data = $(el).data('detail');
    $('.clear').val('');
    $('#md-VendorRekeningBank-edit').modal('show');

	$('#Edit-VendorRekeningBank-CivdId').val(data.civdId);
	$('#Edit-VendorRekeningBank-CompletedDate').val(data.completedDate);
	$('#Edit-VendorRekeningBank-FileSuratPernyataan').val(data.fileSuratPernyataan);
	$('#Edit-VendorRekeningBank-FileSuratPernyataanId').val(data.fileSuratPernyataanId);
	$('#Edit-VendorRekeningBank-IdVendor').val(data.idVendor);
	$('#Edit-VendorRekeningBank-JenisMataUang').val(data.jenisMataUang);
	$('#Edit-VendorRekeningBank-KantorCabang').val(data.kantorCabang);
	$('#Edit-VendorRekeningBank-NamaBank').val(data.namaBank);
	$('#Edit-VendorRekeningBank-Negara').val(data.negara);
	$('#Edit-VendorRekeningBank-NoRekening').val(data.noRekening);
	$('#Edit-VendorRekeningBank-NoRekeningFormat').val(data.noRekeningFormat);
	$('#Edit-VendorRekeningBank-PemegangRekening').val(data.pemegangRekening);


    $('#md-VendorRekeningBank-edit').data('id', data.id);
    $('#VendorRekeningBank-edit_btn').unbind();
    $('#VendorRekeningBank-edit_btn').on('click', function () {
        editVendorRekeningBankSave();
    });
}

function editVendorRekeningBankSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VendorRekeningBank-CivdId').val(),
				completedDate:$('#Edit-VendorRekeningBank-CompletedDate').val(),
				fileSuratPernyataan:$('#Edit-VendorRekeningBank-FileSuratPernyataan').val(),
				fileSuratPernyataanId:$('#Edit-VendorRekeningBank-FileSuratPernyataanId').val(),
				idVendor:$('#Edit-VendorRekeningBank-IdVendor').val(),
				jenisMataUang:$('#Edit-VendorRekeningBank-JenisMataUang').val(),
				kantorCabang:$('#Edit-VendorRekeningBank-KantorCabang').val(),
				namaBank:$('#Edit-VendorRekeningBank-NamaBank').val(),
				negara:$('#Edit-VendorRekeningBank-Negara').val(),
				noRekening:$('#Edit-VendorRekeningBank-NoRekening').val(),
				noRekeningFormat:$('#Edit-VendorRekeningBank-NoRekeningFormat').val(),
				pemegangRekening:$('#Edit-VendorRekeningBank-PemegangRekening').val(),

            }
            RequestData('PUT', `/v1/VendorRekeningBank/edit/${$('#md-VendorRekeningBank-edit').data('id')}`, '#md-VendorRekeningBank-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorRekeningBank-edit').modal('hide');
                    getListVendorRekeningBank();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
