$(document).ready(function () {
    $('#VendorSanksi-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorSanksi-add').modal('show');
        $('#VendorSanksi-create_btn').unbind();
        $('#VendorSanksi-create_btn').on('click', function () {
            addVendorSanksiSave();
        });
    });
});

function addVendorSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VendorSanksi-CivdId').val(),
				filePernyataanPerbaikan:$('#Add-VendorSanksi-FilePernyataanPerbaikan').val(),
				filePernyataanPerbaikanId:$('#Add-VendorSanksi-FilePernyataanPerbaikanId').val(),
				fileSuratSanksi:$('#Add-VendorSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Add-VendorSanksi-FileSuratSanksiId').val(),
				idVendor:$('#Add-VendorSanksi-IdVendor').val(),
				keterangan:$('#Add-VendorSanksi-Keterangan').val(),
				percobaan:$('#Add-VendorSanksi-Percobaan').val(),
				sanksi:$('#Add-VendorSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Add-VendorSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Add-VendorSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Add-VendorSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Add-VendorSanksi-TglPelepasanSanksi').val(),

            }
            RequestData('POST', `/v1/VendorSanksi/add`, '#md-VendorSanksi-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorSanksi-add').modal('hide');
                    getListVendorSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
