	$('#Edit-VendorSanksi-CivdId').val(data.civdId);
	$('#Edit-VendorSanksi-FilePernyataanPerbaikan').val(data.filePernyataanPerbaikan);
	$('#Edit-VendorSanksi-FilePernyataanPerbaikanId').val(data.filePernyataanPerbaikanId);
	$('#Edit-VendorSanksi-FileSuratSanksi').val(data.fileSuratSanksi);
	$('#Edit-VendorSanksi-FileSuratSanksiId').val(data.fileSuratSanksiId);
	$('#Edit-VendorSanksi-IdVendor').val(data.idVendor);
	$('#Edit-VendorSanksi-Keterangan').val(data.keterangan);
	$('#Edit-VendorSanksi-Percobaan').val(data.percobaan);
	$('#Edit-VendorSanksi-Sanksi').val(data.sanksi);
	$('#Edit-VendorSanksi-TglBerakhirPercobaan').val(data.tglBerakhirPercobaan);
	$('#Edit-VendorSanksi-TglBerakhirSanksi').val(data.tglBerakhirSanksi);
	$('#Edit-VendorSanksi-TglBerlakuSanksi').val(data.tglBerlakuSanksi);
	$('#Edit-VendorSanksi-TglPelepasanSanksi').val(data.tglPelepasanSanksi);


    $('#md-VendorSanksi-edit').data('id', data.id);
