function editVendorSanksiSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VendorSanksi-CivdId').val(),
				filePernyataanPerbaikan:$('#Edit-VendorSanksi-FilePernyataanPerbaikan').val(),
				filePernyataanPerbaikanId:$('#Edit-VendorSanksi-FilePernyataanPerbaikanId').val(),
				fileSuratSanksi:$('#Edit-VendorSanksi-FileSuratSanksi').val(),
				fileSuratSanksiId:$('#Edit-VendorSanksi-FileSuratSanksiId').val(),
				idVendor:$('#Edit-VendorSanksi-IdVendor').val(),
				keterangan:$('#Edit-VendorSanksi-Keterangan').val(),
				percobaan:$('#Edit-VendorSanksi-Percobaan').val(),
				sanksi:$('#Edit-VendorSanksi-Sanksi').val(),
				tglBerakhirPercobaan:$('#Edit-VendorSanksi-TglBerakhirPercobaan').val(),
				tglBerakhirSanksi:$('#Edit-VendorSanksi-TglBerakhirSanksi').val(),
				tglBerlakuSanksi:$('#Edit-VendorSanksi-TglBerlakuSanksi').val(),
				tglPelepasanSanksi:$('#Edit-VendorSanksi-TglPelepasanSanksi').val(),

            }
            RequestData('PUT', `/v1/VendorSanksi/edit/${$('#md-VendorSanksi-edit').data('id')}`, '#md-VendorSanksi-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorSanksi-edit').modal('hide');
                    getListVendorSanksi();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
