$(document).ready(function () {
    $('#VendorSusunanPengurus-addBtn').on('click', function () {
        $('.clear').val('');
        $('#md-VendorSusunanPengurus-add').modal('show');
        $('#VendorSusunanPengurus-create_btn').unbind();
        $('#VendorSusunanPengurus-create_btn').on('click', function () {
            addVendorSusunanPengurusSave();
        });
    });
});

function addVendorSusunanPengurusSave() {
    ConfirmMessage('Apakah Anda Yakin?', function (isConfirm) {
        if (isConfirm) {
            var param = {
				civdId:$('#Add-VendorSusunanPengurus-CivdId').val(),
				completedDate:$('#Add-VendorSusunanPengurus-CompletedDate').val(),
				email:$('#Add-VendorSusunanPengurus-Email').val(),
				fileKtpKitas:$('#Add-VendorSusunanPengurus-FileKtpKitas').val(),
				fileKtpKitasId:$('#Add-VendorSusunanPengurus-FileKtpKitasId').val(),
				fileTandaTangan:$('#Add-VendorSusunanPengurus-FileTandaTangan').val(),
				fileTandaTanganId:$('#Add-VendorSusunanPengurus-FileTandaTanganId').val(),
				idVendor:$('#Add-VendorSusunanPengurus-IdVendor').val(),
				jabatan:$('#Add-VendorSusunanPengurus-Jabatan').val(),
				nama:$('#Add-VendorSusunanPengurus-Nama').val(),
				noKtpKitas:$('#Add-VendorSusunanPengurus-NoKtpKitas').val(),
				tipePengurus:$('#Add-VendorSusunanPengurus-TipePengurus').val(),

            }
            RequestData('POST', `/v1/VendorSusunanPengurus/add`, '#md-VendorSusunanPengurus-add', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Disimpan", "success");
                    $('#md-VendorSusunanPengurus-add').modal('hide');
                    getListVendorSusunanPengurus();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
