function editVendorSusunanPengurusSave() {
    ConfirmMessage('Apakah Anda Yakin Akan Mengubah Data Ini?', isConfirm => {
        if (isConfirm) {
            var param = {
				civdId:$('#Edit-VendorSusunanPengurus-CivdId').val(),
				completedDate:$('#Edit-VendorSusunanPengurus-CompletedDate').val(),
				email:$('#Edit-VendorSusunanPengurus-Email').val(),
				fileKtpKitas:$('#Edit-VendorSusunanPengurus-FileKtpKitas').val(),
				fileKtpKitasId:$('#Edit-VendorSusunanPengurus-FileKtpKitasId').val(),
				fileTandaTangan:$('#Edit-VendorSusunanPengurus-FileTandaTangan').val(),
				fileTandaTanganId:$('#Edit-VendorSusunanPengurus-FileTandaTanganId').val(),
				idVendor:$('#Edit-VendorSusunanPengurus-IdVendor').val(),
				jabatan:$('#Edit-VendorSusunanPengurus-Jabatan').val(),
				nama:$('#Edit-VendorSusunanPengurus-Nama').val(),
				noKtpKitas:$('#Edit-VendorSusunanPengurus-NoKtpKitas').val(),
				tipePengurus:$('#Edit-VendorSusunanPengurus-TipePengurus').val(),

            }
            RequestData('PUT', `/v1/VendorSusunanPengurus/edit/${$('#md-VendorSusunanPengurus-edit').data('id')}`, '#md-VendorSusunanPengurus-edit .modal-content', null, JSON.stringify(param), function (data_obj) {
                if (data_obj.succeeded) {
                    ShowNotif("Data Berhasil Dirubah", "success");
                    $('#md-VendorSusunanPengurus-edit').modal('hide');
                    getListVendorSusunanPengurus();
                }
                else if (data_obj.code == 401) { //unathorized
                    AlertMessage(data_obj.message);
                } else
                    ShowNotif(`${data_obj.message} : ${data_obj.description}`, "error");
            });
        }
    });
}
